      CREATE   VIEW hulp1(art) AS          SELECT   art          FROM   artikel          WHERE   prijs > 3000;           CREATE   VIEW hulp2(klant, aantal) AS          SELECT   klant, COUNT(DISTINCT art)          FROM   verkoop          WHERE   hoeveelheid = 1             AND art IN (   SELECT   art                FROM   hulp1)          GROUP   BY klant;           SELECT   klant       FROM   hulp2       WHERE   aantal = (   SELECT   COUNT(art)             FROM   hulp1);           DROP VIEW hulp2;       DROP VIEW hulp1;       
