      SELECT   afdeling.afd, afdnaam, COUNT(w)       FROM   werknemer, afdeling       WHERE   werknemer.afd = afdeling.afd       GROUP   BY afdeling.afd, afdnaam       HAVING   COUNT(w) > (   SELECT   COUNT(w)             FROM   werknemer             WHERE   afd IN (   SELECT   afd                   FROM   werknemer                   WHERE   naam = "Opperman"                      AND voorl = "F"));       
