      CREATE   VIEW hulp1(klant, totjan) AS          SELECT   klant, SUM(bedrag)          FROM   verkoop          WHERE   year(datum) = 2002             AND month(datum) = 1          GROUP   BY klant;           CREATE   VIEW hulp2(klant, totjuli) AS          SELECT   klant, SUM(bedrag)          FROM   verkoop          WHERE   year(datum) = 2002             AND month(datum) = 7          GROUP   BY klant;           SELECT   hulp1.klant       FROM   hulp1, hulp2       WHERE   hulp1.klant = hulp2.klant          AND hulp1.totjan > hulp2.totjuli;           DROP VIEW hulp1;       DROP VIEW hulp2;       
