      CREATE   VIEW hulp1(plaats, aantklant) AS          SELECT   woonplaats, COUNT(klant)          FROM   klant          GROUP   BY woonplaats;           CREATE   VIEW hulp2(plaats, aantwerkn) AS          SELECT   woonplaats, COUNT(w)          FROM   werknemer          GROUP   BY woonplaats;           SELECT   hulp1.plaats       FROM   hulp1, hulp2       WHERE   hulp1.plaats = hulp2.plaats          AND aantklant > aantwerkn;           DROP VIEW hulp1;       DROP VIEW hulp2;       
