      CREATE   VIEW hulp1(afd, totwerkn) AS          SELECT   afd, COUNT(w)          FROM   werknemer          WHERE   woonplaats = "Den Haag"          GROUP   BY afd;           CREATE   VIEW hulp2(afd, totwerkn) AS          SELECT   afd, COUNT(w)          FROM   werknemer          WHERE   woonplaats = "Zoetermeer"          GROUP   BY afd;           SELECT   hulp1.afd       FROM   hulp1, hulp2       WHERE   hulp1.afd = hulp2.afd          AND hulp2.totwerkn > hulp1.totwerkn;           DROP VIEW hulp1;       DROP VIEW hulp2;       
