      SELECT   DISTINCT afdeling.afd, chefnaam, srtc       FROM   afdeling, verkart, artikel A1       WHERE   afdeling.afd = verkart.afd          AND verkart.art = A1.art          AND verdieping = 0       GROUP   BY afdeling.afd, chefnaam, srtc       HAVING   COUNT(verkart.art) > (   SELECT   COUNT(art) * 0.5             FROM   artikel A2             WHERE   A1.srtc = A2.srtc);       
