      CREATE   VIEW hulp1(art) AS          SELECT   art          FROM   inkart          WHERE   prijs > 650          GROUP   BY art          HAVING   COUNT(lev) > 1;           CREATE   VIEW hulp2(klant, aantal) AS          SELECT   klant, COUNT(DISTINCT art)          FROM   verkoop          WHERE   art IN (   SELECT   art                FROM   hulp1)          GROUP   BY klant;           SELECT   naam, voorl       FROM   klant, hulp2       WHERE   klant.klant = hulp2.klant          AND aantal = (   SELECT   COUNT(art)             FROM   hulp1);           DROP VIEW hulp2;       DROP VIEW hulp1;       
