      CREATE   VIEW hulp1(afd, aantart) AS          SELECT   afdeling.afd, COUNT(art)          FROM   afdeling, verkart          WHERE   afdeling.afd = verkart.afd             AND verdieping = 2          GROUP   BY afdeling.afd;           CREATE   VIEW hulp2(w, naam, woonplaats) AS          SELECT   w, naam, woonplaats          FROM   werknemer          WHERE   afd IN (   SELECT   afd                FROM   hulp1                WHERE   aantart = (   SELECT   MAX(aantart)                      FROM   hulp1));           SELECT   w, naam       FROM   hulp2       WHERE   woonplaats IN (   SELECT   vestplaats             FROM   leverancier);           DROP VIEW hulp2;       DROP VIEW hulp1;       
