      CREATE   VIEW hulp1(afd, aantw) AS          SELECT   afd, COUNT(w)          FROM   werknemer          GROUP   BY afd;           CREATE   VIEW hulp2(afd, aantv) AS       SELECT   afd, COUNT(art)       FROM   verkart       GROUP   BY afd;           SELECT   afd       FROM   hulp1       WHERE   aantw > (   SELECT   AVG(aantw)             FROM   hulp1)          AND afd IN (   SELECT   afd             FROM   hulp2             WHERE   aantv > (   SELECT   AVG(aantv)                   FROM   hulp2));           DROP VIEW hulp1;       DROP VIEW hulp2;       
