      CREATE   VIEW hulp (art, marge) AS          SELECT   artikel.art, (artikel.prijs - inkart.prijs)          FROM   artikel, inkart          WHERE   artikel.art = inkart.art;           SELECT   afd, hulp.art, beschrijving, srtc       FROM   artikel, hulp, verkart       WHERE   artikel.art = hulp.art          AND hulp.art = verkart.art          AND marge = (   SELECT   MAX(marge)             FROM   hulp);           DROP VIEW hulp;       
