      CREATE   VIEW hulp1(afd) AS          SELECT   DISTINCT afd          FROM   verkart, artikel          WHERE   verkart.art = artikel.art             AND kleur = "rood";           CREATE   VIEW hulp2(plaats, aantal) AS          SELECT   woonplaats, COUNT(w)          FROM   werknemer          WHERE   afd IN (   SELECT afd                FROM hulp1)          GROUP   BY woonplaats;           SELECT   DISTINCT woonplaats       FROM   klant       WHERE   woonplaats IN (   SELECT   plaats             FROM   hulp2             WHERE   aantal = (   SELECT   MAX(aantal)                   FROM   hulp2));           DROP VIEW hulp2;       DROP VIEW hulp1;       
