      CREATE   VIEW hulp(lev, geld) AS          SELECT   inkart.lev, SUM(hoeveelheid * prijs)          FROM   leverantie, inkart          WHERE   leverantie.art = inkart.art             AND leverantie.lev = inkart.lev          GROUP   BY inkart.lev;           SELECT   lev, geld       FROM   hulp       WHERE   geld = (   SELECT   MAX(geld)             FROM   hulp);           DROP VIEW hulp;       
