      CREATE   VIEW hulp(klant, aantal) AS          SELECT   klant, COUNT(DISTINCT srtc)          FROM   verkoop, artikel          WHERE   verkoop.art = artikel.art             AND srtc IN (   SELECT   srtc                FROM   inkart, artikel                WHERE   inkart.art = artikel.art                   AND lev = 1)          GROUP   BY klant;           SELECT   klant       FROM   hulp       WHERE   aantal = (   SELECT   COUNT(DISTINCT srtc)             FROM   inkart, artikel             WHERE   inkart.art = artikel.art                AND lev = 1);           DROP VIEW hulp;       
