      CREATE   VIEW hulp(klant, bedrag) AS          SELECT   klant, SUM(bedrag)          FROM   verkoop, afdeling          WHERE   verkoop.afd = afdeling.afd             AND afdnaam = "Sport"          GROUP   BY klant;           SELECT   naam       FROM   klant, hulp       WHERE   klant.klant = hulp.klant          AND bedrag = (   SELECT   MAX(bedrag)             FROM   hulp);           DROP VIEW hulp;       
