      CREATE   VIEW hulp1(art, verkhoev) AS          SELECT   art, SUM(hoeveelheid)          FROM   verkoop          WHERE   year(datum) = 2002             AND month(datum) < 7          GROUP   BY art;           CREATE   VIEW hulp2(art, levhoev) AS          SELECT   art, SUM(hoeveelheid)          FROM   leverantie          WHERE   year(datum) = 2002             AND month(datum) < 7          GROUP   BY art;           SELECT   COUNT(hulp1.art)       FROM   hulp1, hulp2       WHERE   hulp1.art = hulp2.art          AND hulp1.verkhoev < hulp2.levhoev;           DROP VIEW hulp1;       DROP VIEW hulp2;       
