      CREATE   VIEW hulp1(klant, hschuld) AS          SELECT   klant, SUM(bedrag - aanbet)          FROM   verkoop          WHERE   year(datum) = 2002             AND month(datum) = 11          GROUP   BY klant;           CREATE   VIEW hulp2(klant, hschuld) AS          SELECT   klant, SUM(bedrag - aanbet)          FROM   verkoop          WHERE   year(datum) = 2003             AND month(datum) = 3          GROUP   BY klant;           SELECT   hulp1.klant       FROM   hulp1, hulp2       WHERE   hulp1.klant = hulp2.klant          AND hulp2.hschuld > hulp1.hschuld;           DROP VIEW hulp1;       DROP VIEW hulp2;       
