      CREATE   VIEW hulp(afd, aantal) AS          SELECT   afd, COUNT(verkart.art)          FROM   verkart, artikel          WHERE   verkart.art IN (   SELECT   art                FROM   verkart                WHERE   afd = 14)             AND verkart.art = artikel.art             AND kleur = "geel"          GROUP   BY afd;           SELECT   hulp.afd, afdnaam       FROM   hulp, afdeling       WHERE   hulp.afd = afdeling.afd          AND aantal = (   SELECT   COUNT(verkart.art)             FROM   verkart, artikel             WHERE   afd = 14                AND verkart.art = artikel.art                AND kleur = "geel");           DROP VIEW hulp;       
