      CREATE   VIEW hulp (lev, aantart) AS          SELECT   lev, COUNT(art)          FROM   inkart          WHERE   prijs < 500          GROUP   BY lev;           SELECT   leverancier.lev, firmanaam       FROM   leverancier, inkart       WHERE   leverancier.lev = inkart.lev       GROUP   BY leverancier.lev, firmanaam       HAVING   COUNT(art) = (   SELECT   aantart             FROM   hulp             WHERE   leverancier.lev = hulp.lev);           DROP VIEW hulp;       
