      CREATE   VIEW hulp1(srtc, aantal) AS          SELECT   srtc, COUNT(lev)          FROM   artikel, inkart          WHERE   artikel.art = inkart.art          GROUP   BY srtc;           CREATE   VIEW hulp2(lev, art, prijs) AS          SELECT   lev, inkart.art, inkart.prijs          FROM   inkart, artikel          WHERE   inkart.art = artikel.art             AND srtc IN (   SELECT   srtc                FROM   hulp1                WHERE   aantal = (   SELECT   MAX(aantal)                      FROM   hulp1));           SELECT   lev, art, prijs       FROM   hulp2       WHERE   prijs = (   SELECT   MIN(prijs)             FROM   hulp2);           DROP VIEW hulp2;       DROP VIEW hulp1;       
