      CREATE   VIEW hulp(klant, aantal) AS          SELECT   klant, COUNT(DISTINCT art)          FROM   verkoop          WHERE   art IN (   SELECT   DISTINCT art                FROM   verkoop                WHERE   klant = 3)          GROUP   BY klant;           SELECT   klant.klant, naam       FROM   hulp, klant       WHERE   hulp.klant = klant.klant          AND aantal >= (   SELECT   COUNT(DISTINCT art)             FROM   verkoop             WHERE   klant = 3);           DROP VIEW hulp;       
