      CREATE   VIEW hulp(afd) AS          SELECT   DISTINCT afd          FROM   verkart, inkart, leverancier          WHERE   verkart.art = inkart.art             AND inkart.lev = leverancier.lev             AND firmanaam = "Willemse";           SELECT   DISTINCT firmanaam       FROM   verkart, inkart, leverancier       WHERE   verkart.art = inkart.art          AND inkart.lev = leverancier.lev          AND firmanaam <> "Willemse"          AND afd IN (   SELECT   afd             FROM   hulp);           DROP VIEW hulp;       
