      CREATE   VIEW hulp1(klant) AS          SELECT   DISTINCT klant          FROM   verkoop, artikel          WHERE   verkoop.art = artikel.art             AND prijs >= 10          GROUP   BY klant          HAVING   COUNT(DISTINCT afd) > 4;           SELECT   hulp1.klant, COUNT(verk)       FROM   hulp1, verkoop       WHERE   verkoop.klant = hulp1.klant       GROUP   BY hulp1.klant       HAVING   COUNT(verk) > 7;           DROP VIEW hulp1;       
